// src/components/InventoryList.js
import React from 'react';

const InventoryList = ({ items, onDelete, onEdit }) => {
  const totalProfit = items.reduce((sum, item) => {
    const profitPerItem = item.price - item.cost;
    return sum + profitPerItem * item.quantity;
  }, 0);

  return (
    <div>
      <h3>Total Estimated Profit: ₹{totalProfit.toFixed(2)}</h3>
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Item</th>
            <th>Qty</th>
            <th>Price</th>
            <th>Cost</th>
            <th>Profit/Item</th>
            <th>Reorder Level</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => {
            const isLowStock = item.quantity <= item.reorderLevel;
            const profit = item.price - item.cost;

            return (
              <tr key={item.id} style={{ backgroundColor: isLowStock ? '#ffe0e0' : 'white' }}>
                <td>{item.name}</td>
                <td>{item.quantity}</td>
                <td>₹{item.price.toFixed(2)}</td>
                <td>₹{item.cost.toFixed(2)}</td>
                <td>₹{profit.toFixed(2)}</td>
                <td>{item.reorderLevel}</td>
                <td>{isLowStock ? '⚠️ Reorder Needed' : '✔ OK'}</td>
                <td>
                  <button onClick={() => onEdit(item)}>Edit</button>
                  <button onClick={() => onDelete(item.id)}>Delete</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default InventoryList;
