import React, { useState } from 'react';
import InventoryForm from './components/InventoryForm';
import InventoryList from './components/InventoryList';
import './App.css';

function App() {
  const [items, setItems] = useState([]);
  const [editItem, setEditItem] = useState(null);
  const [filter, setFilter] = useState('');

  const addItem = (item) => {
    if (editItem) {
      setItems(items.map(i => (i.id === editItem.id ? item : i)));
      setEditItem(null);
    } else {
      setItems([...items, { ...item, id: Date.now() }]);
    }
  };

  const deleteItem = (id) => {
    setItems(items.filter(item => item.id !== id));
  };

  const totalValue = items.reduce((acc, item) => acc + item.quantity * item.price, 0);
  const lowStockItems = items.filter(item => item.quantity < 5);
  const filteredItems = filter
    ? items.filter(item => item.category === filter)
    : items;

  return (
    <div className="app">
      <h1>Inventory Management System</h1>
      <InventoryForm onAddItem={addItem} editItem={editItem} />

      <div className="info-bar">
        <h3>Total Inventory Value: ${totalValue.toFixed(2)}</h3>
        <select onChange={(e) => setFilter(e.target.value)}>
          <option value="">All Categories</option>
          <option value="Electronics">Electronics</option>
          <option value="Groceries">Groceries</option>
          <option value="Stationery">Stationery</option>
        </select>
      </div>

      {lowStockItems.length > 0 && (
        <div className="alert">
          ⚠️ Low stock: {lowStockItems.map(i => i.name).join(', ')}
        </div>
      )}

      <InventoryList
        items={filteredItems}
        onDelete={deleteItem}
        onEdit={setEditItem}
      />
    </div>
  );
}

export default App;
