import React, { useState } from 'react';
import './styles.css';

const items = [
  { id: 1, name: 'Laptop', category: 'Electronics' },
  { id: 2, name: 'Desk', category: 'Furniture' },
  { id: 3, name: 'Monitor', category: 'Electronics' },
  { id: 4, name: 'Office Chair', category: 'Furniture' },
];

function App() {
  const [inventory, setInventory] = useState([]);
  const [showItemPanel, setShowItemPanel] = useState(false);

  const handleAddItem = (item) => {
    setInventory([...inventory, { ...item, addedAt: new Date().toLocaleString() }]);
    setShowItemPanel(false);
  };

  return (
    <div className="app">
      <header className="header">Business Inventory Manager</header>

      <main className="main">
        <button className="add-button" onClick={() => setShowItemPanel(!showItemPanel)}>
          {showItemPanel ? 'Close Item List' : 'Add Item'}
        </button>

        <div className={`side-panel ${showItemPanel ? 'open' : ''}`}>
          <h3>Select Item to Add</h3>
          {items.map((item) => (
            <div key={item.id} className="item-card" onClick={() => handleAddItem(item)}>
              <strong>{item.name}</strong>
              <span>{item.category}</span>
            </div>
          ))}
        </div>

        <section className="inventory-section">
          <h2>Current Inventory</h2>
          {inventory.length === 0 ? (
            <p className="empty">No items added yet. Click "Add Item" to begin.</p>
          ) : (
            <table className="inventory-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Category</th>
                  <th>Added At</th>
                </tr>
              </thead>
              <tbody>
                {inventory.map((item, idx) => (
                  <tr key={idx}>
                    <td>{item.name}</td>
                    <td>{item.category}</td>
                    <td>{item.addedAt}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </section>
      </main>
    </div>
  );
}

export default App;
