import React, { useState } from 'react';
import './styles.css';

const predefinedItems = [
  { id: 1, name: 'Printer', category: 'Electronics' },
  { id: 2, name: 'Laptop', category: 'Electronics' },
  { id: 3, name: 'Chair', category: 'Furniture' },
  { id: 4, name: 'Desk', category: 'Furniture' }
];

function App() {
  const [inventory, setInventory] = useState([]);
  const [showItems, setShowItems] = useState(false);

  const handleAddItem = (item) => {
    setInventory([...inventory, { ...item, addedAt: new Date().toLocaleString() }]);
    setShowItems(false);
  };

  return (
    <div className="app">
      <header className="header">Inventory Management System</header>

      <div className="content">
        <button className="add-btn" onClick={() => setShowItems(!showItems)}>
          {showItems ? 'Hide Item List' : 'Add Item'}
        </button>

        {showItems && (
          <div className="item-list">
            {predefinedItems.map(item => (
              <div key={item.id} className="item" onClick={() => handleAddItem(item)}>
                {item.name} - {item.category}
              </div>
            ))}
          </div>
        )}

        <h2 className="inventory-title">Inventory List</h2>
        <table className="inventory-table">
          <thead>
            <tr>
              <th>Item</th>
              <th>Category</th>
              <th>Added At</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map((item, idx) => (
              <tr key={idx}>
                <td>{item.name}</td>
                <td>{item.category}</td>
                <td>{item.addedAt}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
