// src/components/InventoryForm.js
import React, { useState, useEffect } from 'react';

const InventoryForm = ({ onAddItem, editItem }) => {
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [cost, setCost] = useState('');
  const [reorderLevel, setReorderLevel] = useState('');

  useEffect(() => {
    if (editItem) {
      setName(editItem.name);
      setQuantity(editItem.quantity);
      setPrice(editItem.price);
      setCost(editItem.cost);
      setReorderLevel(editItem.reorderLevel);
    }
  }, [editItem]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !quantity || !price || !cost || !reorderLevel) return;

    const item = {
      id: editItem ? editItem.id : Date.now(),
      name,
      quantity: parseInt(quantity),
      price: parseFloat(price),
      cost: parseFloat(cost),
      reorderLevel: parseInt(reorderLevel)
    };

    onAddItem(item);
    setName('');
    setQuantity('');
    setPrice('');
    setCost('');
    setReorderLevel('');
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <input type="text" placeholder="Item Name" value={name} onChange={(e) => setName(e.target.value)} />
      <input type="number" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
      <input type="number" placeholder="Selling Price" value={price} onChange={(e) => setPrice(e.target.value)} />
      <input type="number" placeholder="Cost Price" value={cost} onChange={(e) => setCost(e.target.value)} />
      <input type="number" placeholder="Reorder Level" value={reorderLevel} onChange={(e) => setReorderLevel(e.target.value)} />
      <button type="submit">{editItem ? 'Update' : 'Add Item'}</button>
    </form>
  );
};

export default InventoryForm;
