import React, { useState } from 'react';
import './styles.css';

const items = [
  { id: 1, name: 'Laptop', category: 'Electronics', price: 85000, discount: 5, review: "Great for work." },
  { id: 2, name: 'Mouse', category: 'Electronics', price: 500, discount: 10, review: "Smooth and precise." },
  { id: 3, name: 'Keyboard', category: 'Electronics', price: 1200, discount: 8, review: "Comfortable typing." },
  { id: 4, name: 'Monitor', category: 'Electronics', price: 10000, discount: 7, review: "Crisp display." },
  { id: 5, name: 'Printer', category: 'Electronics', price: 6000, discount: 12, review: "Fast printing." },
  { id: 6, name: 'Desk', category: 'Furniture', price: 7000, discount: 6, review: "Spacious and strong." },
  { id: 7, name: 'Office Chair', category: 'Furniture', price: 5500, discount: 5, review: "Ergonomic design." },
  { id: 8, name: 'Bookshelf', category: 'Furniture', price: 4000, discount: 15, review: "Sleek design." },
  { id: 9, name: 'Filing Cabinet', category: 'Furniture', price: 3500, discount: 10, review: "Organized storage." },
  { id: 10, name: 'Pen Pack', category: 'Stationery', price: 150, discount: 20, review: "Smooth ink flow." },
  { id: 11, name: 'Notebook', category: 'Stationery', price: 200, discount: 15, review: "Great quality paper." },
  { id: 12, name: 'Sticky Notes', category: 'Stationery', price: 100, discount: 25, review: "Strong adhesive." },
  { id: 13, name: 'Whiteboard', category: 'Office Supplies', price: 2500, discount: 8, review: "Clear writing." },
  { id: 14, name: 'Projector', category: 'Electronics', price: 30000, discount: 10, review: "Bright projection." },
  { id: 15, name: 'Extension Cord', category: 'Electronics', price: 800, discount: 5, review: "Reliable power." },
  { id: 16, name: 'Router', category: 'Electronics', price: 2200, discount: 7, review: "Strong signal." },
  { id: 17, name: 'Scissors', category: 'Stationery', price: 120, discount: 18, review: "Sharp and safe." },
  { id: 18, name: 'Paper Ream', category: 'Stationery', price: 450, discount: 20, review: "Bright white." },
  { id: 19, name: 'Stapler', category: 'Stationery', price: 350, discount: 10, review: "Durable build." },
  { id: 20, name: 'USB Drive', category: 'Electronics', price: 1000, discount: 5, review: "Fast transfers." },
];

function App() {
  const [inventory, setInventory] = useState([]);
  const [showItemPanel, setShowItemPanel] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  const handleAddItem = (item) => {
    const finalPrice = item.price - (item.price * item.discount) / 100;
    setInventory([
      ...inventory,
      { ...item, finalPrice, addedAt: new Date().toLocaleString() }
    ]);
    setShowItemPanel(false);
    setSelectedItem(null);
  };

  const handleSelectInventory = (item) => {
    setSelectedItem(item);
  };

  const totalValue = inventory.reduce((sum, item) => sum + item.finalPrice, 0);

  return (
    <div className="app">
      {/* Professional header */}
      <header className="header">
        <div className="header-content">
          <div className="logo">📦</div>
          <div>
            <h1 className="title">Inventory Management System</h1>
            <p className="subtitle">Efficient. Reliable. Organized.</p>
          </div>
        </div>
      </header>

      <main className="main">
        <button
          className="add-button"
          onClick={() => setShowItemPanel(!showItemPanel)}
        >
          {showItemPanel ? 'Close Item List' : 'Add Item'}
        </button>

        {/* Animated side panel for choosing items */}
        <div className={`side-panel ${showItemPanel ? 'open' : ''}`}>
          <h3>Select Item to Add</h3>
          {items.map((item) => (
            <div
              key={item.id}
              className="item-card"
              onClick={() => handleAddItem(item)}
            >
              <strong>{item.name}</strong>
              <p>
                ₹{item.price} | {item.discount}% Off
              </p>
              <small>"{item.review}"</small>
            </div>
          ))}
        </div>

        <section className="inventory-section">
          <h2>Current Inventory</h2>
          {inventory.length === 0 ? (
            <p className="empty">
              No items added yet. Click "Add Item" to begin.
            </p>
          ) : (
            <table className="inventory-table">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Name</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th>Discount</th>
                  <th>Final Price</th>
                  <th>Review</th>
                  <th>Added At</th>
                </tr>
              </thead>
              <tbody>
                {inventory.map((item, idx) => (
                  <tr key={idx} onClick={() => handleSelectInventory(item)}>
                    <td>{idx + 1}</td>
                    <td>{item.name}</td>
                    <td>{item.category}</td>
                    <td>₹{item.price}</td>
                    <td>{item.discount}%</td>
                    <td>₹{item.finalPrice.toFixed(2)}</td>
                    <td>{item.review}</td>
                    <td>{item.addedAt}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </section>

        {/* Details panel for selected inventory item */}
        {selectedItem && (
          <div className="details-panel">
            <h3>Item Details</h3>
            <p>
              <strong>Name:</strong> {selectedItem.name}
            </p>
            <p>
              <strong>Category:</strong> {selectedItem.category}
            </p>
            <p>
              <strong>Price:</strong> ₹{selectedItem.price}
            </p>
            <p>
              <strong>Discount:</strong> {selectedItem.discount}%
            </p>
            <p>
              <strong>Final Price:</strong> ₹{selectedItem.finalPrice.toFixed(2)}
            </p>
            <p>
              <strong>Review:</strong> {selectedItem.review}
            </p>
            <p>
              <strong>Added At:</strong> {selectedItem.addedAt}
            </p>
          </div>
        )}

        {/* Total bill display */}
        {inventory.length > 0 && (
          <div className="total-value">
            <strong>Total Bill:</strong> ₹{totalValue.toFixed(2)}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
